CREATE TABLE sch.Customers (  CustomerID serial NOT NULL PRIMARY KEY,  CustomerName text NOT NULL,  CustomerMail text,  CustomerPhone varchar(16),  CustomerType varchar(10) NOT NULL);CREATE TABLE sch.Designers (  DesignerID serial NOT NULL PRIMARY KEY,  DesignerName text NOT NULL,  DesignerMail text,  DesignerPhone varchar(16),  DOB varchar(10),  DesignerPos varchar(12) NOT NULL,  Specialization varchar(11) NOT NULL);CREATE TABLE sch.TechTasks (  TaskID serial NOT NULL PRIMARY KEY,  TaskName text NOT NULL,  Software text,  Print char(1) NOT NULL,  Refs char(1) NOT NULL,  Deadline char(10));CREATE TABLE sch.Projects (  ProjectID serial NOT NULL PRIMARY KEY,  CustomerID int REFERENCES sch.Customers(CustomerID),  DesignerID int REFERENCES sch.Designers(DesignerID),  TaskID int UNIQUE REFERENCES sch.TechTasks(TaskID),  ProjectName text NOT NULL,  ProjectType varchar(12) NOT NULL,  Status varchar(4) NOT NULL);CREATE TABLE sch.Distribution (	ProjectID int NOT NULL,	DesignerID int NOT NULL)